import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { DiaryStateContext, handleEditContext } from "../App";
import { Button } from "../components/Button";
import { Header } from "../components/Header";
import MainList from "../components/MainList";

export default function Home() {
  const [data, setData] = useState([]);
  const diaryList = useContext(DiaryStateContext);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [filter, setFilter] = useState("");
  useEffect(() => {
    if (diaryList && diaryList.length >= 1) {
      const firstDay = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        1
      ).getTime();
      const lastDay = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth() + 1,
        0
      ).getTime();
      setData(
        diaryList.filter((it) => firstDay <= it.date && it.date <= lastDay)
      );
    }
  }, [diaryList, currentDate]);

  const handleIncreaseMonth = () => {
    setCurrentDate(
      new Date(
        currentDate.getFullYear(),
        currentDate.getMonth() + 1,
        currentDate.getDate()
      )
    );
  };
  const handleDecreaseMonth = () => {
    new Date(
      setCurrentDate(
        new Date(
          currentDate.getFullYear(),
          currentDate.getMonth() - 1,
          currentDate.getDate()
        )
      )
    );
  };
  const copyList = JSON.parse(JSON.stringify(data));
  const getProcessedDiaryList = (e) => {
    const compare = (a, b) => {
      if (e.target.value === "최신순") {
        return parseInt(b.date) - parseInt(a.date);
      } else {
        return parseInt(a.date) - parseInt(b.date);
      }
    };
    const sortedList = copyList.sort(compare);
    return sortedList;
  };
  const handleTimeFilter = (e) => {
    setData(getProcessedDiaryList(e));
  };
  const handleMoodFilter = (e) => {
    if (e.target.value === "모두") {
      setData(data);
    } else if (e.target.value === "좋은 기억만") {
      setData(data.filter((item) => parseInt(item.emotion) <= 3));
    } else if (e.target.value === "안좋은 기억만") {
      setData(data.filter((item) => parseInt(item.emotion) > 3));
    }
  };
  return (
    <div className="Home">
      <Header
        headText={`${currentDate.getFullYear()}년 ${
          currentDate.getMonth() + 1
        }월`}
        leftChild={
          <Button text={"<"} type={"default"} onClick={handleDecreaseMonth} />
        }
        rightChild={
          <Button text={">"} type={"default"} onClick={handleIncreaseMonth} />
        }
      />
      <ul className="lists">
        {data.map((item, idx) => (
          <Link to="/diary">
            <MainList
              key={idx}
              id={item.id}
              date={item.date}
              emotion={item.emotion}
              content={item.content}
            />
          </Link>
        ))}
      </ul>
    </div>
  );
}
