import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button } from "../components/Button";
import { Header } from "../components/Header";
import { Moods } from "../components/Moods";

export default function New() {
  const [mood, setMood] = useState(3);
  const [text, setText] = useState("");
  const moodList = [
    {
      mood: 1,
      text: "완전 좋음",
    },
    {
      mood: 2,
      text: "좋음",
    },
    {
      mood: 3,
      text: "그럭저럭",
    },
    {
      mood: 4,
      text: "나쁨",
    },
    {
      mood: 5,
      text: "끔찍함",
    },
  ];
  const handleTextArea = (e) => {
    e.preventDefault();
    console.log("RESIZE!");
  };
  const handleMoodsToggle = (e) => {
    e.target.classList.add(`mood_active`);
    console.log(e.target.mood);
  };
  return (
    <div className="New">
      <Header
        headText={"새 일기쓰기"}
        leftChild={
          <Link to="/">
            <Button text={"< 뒤로가기"} type={"default"} />
          </Link>
        }
      />
      <section className="New__date">
        <h4>오늘은 언제인가요?</h4>
        <input type="date" />
      </section>
      <section className="New__mood">
        <h4>오늘의 감정</h4>
        <div className="moods">
          {moodList.map((item) => {
            return (
              <Moods
                onClick={handleMoodsToggle}
                mood={item.mood}
                text={item.text}
              />
            );
          })}
        </div>
      </section>
      <section className="New__text">
        <h4>오늘의 일기</h4>
        <textarea onResize={handleTextArea}></textarea>
      </section>
      <div className="New__btn">
        <Link to="/">
          <Button text={"취소하기"} type={"default"} />
        </Link>
        <Link to="/">
          <Button text={"작성완료"} type={"positive"} />
        </Link>
      </div>
    </div>
  );
}
