import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button } from "../components/Button";
import { Header } from "../components/Header";
import { Moods } from "../components/Moods";

export default function New() {
  const [mood, setMood] = useState(3);
  const [text, setText] = useState("");
  const emotionList = [
    {
      emotion_mood: 1,
      emotion_textemotion_: "완전 좋음",
    },
    {
      emotion_mood: 2,
      emotion_textemotion_: "좋음",
    },
    {
      emotion_mood: 3,
      emotion_textemotion_: "그럭저럭",
    },
    {
      emotion_mood: 4,
      emotion_textemotion_: "나쁨",
    },
    {
      emotion_mood: 5,
      emotion_textemotion_: "끔찍함",
    },
  ];
  const handleTextArea = (e) => {
    e.preventDefault();
    console.log("RESIZE!");
  };
  const handleMoodsToggle = (e) => {
    console.log("Click!");
  };
  return (
    <div className="New">
      <Header
        headText={"새 일기쓰기"}
        leftChild={
          <Link to="/">
            <Button text={"< 뒤로가기"} type={"default"} />
          </Link>
        }
      />
      <section className="New__date">
        <h4>오늘은 언제인가요?</h4>
        <input type="date" />
      </section>
      <section className="New__mood">
        <h4>오늘의 감정</h4>
        <div className="moods">
          {emotionList.map((item) => {
            return (
              <Moods
                handleMoodsToggle={handleMoodsToggle}
                mood={item.emotion_mood}
                text={item.emotion_text}
              />
            );
          })}
        </div>
      </section>
      <section className="New__text">
        <h4>오늘의 일기</h4>
        <textarea></textarea>
      </section>
      <div className="New__btn">
        <Link to="/">
          <Button text={"취소하기"} type={"default"} />
        </Link>
        <Link to="/">
          <Button text={"작성완료"} type={"positive"} />
        </Link>
      </div>
    </div>
  );
}
