import React, { useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import { handleEditContext } from "../App";
import { Button } from "./Button";
import { Moods } from "./Moods";

export default function MainList({ id, date, emotion, content }) {
  const pastDate = new Date(date);
  const setInfo = useContext(handleEditContext);
  const handleEdit = () => {
    setInfo.setEditInfo(id, date, content, emotion);
  };

  return (
    <>
      <div className="control-bar">
        <select className="option-date" onChange={handleTimeFilter}>
          <option value="최신순">최신순</option>
          <option value="오래된 순">오래된 순</option>
        </select>
        <select className="option-mood" onChange={handleMoodFilter}>
          <option value="모두">모두</option>
          <option value="좋은 기억만">좋은 기억만</option>
          <option value="안좋은 기억만">안좋은 기억만</option>
        </select>
        <Link to="/new">
          <Button
            type={"positive"}
            text={"새 일기쓰기"}
            className={"btn-create"}
          />
        </Link>
      </div>
      <li className="list" onClick={handleEdit}>
        <div className="list-img">
          <Moods mood={emotion} />
        </div>
        <div className="list-content">
          <div className="content">
            <div className="content-date">
              {`${pastDate.getFullYear()}. ${
                pastDate.getMonth() + 1
              } .${pastDate.getDate()}`}
            </div>
            <div className="content-text">{content}</div>
          </div>
          <div className="content-edit-btn">
            <Link to="/edit">
              <Button text={"수정하기"} type={"default"} onClick={handleEdit} />
            </Link>
          </div>
        </div>
      </li>
    </>
  );
}
