import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { handleEditContext } from "../App";
import { Button } from "./Button";
import { Moods } from "./Moods";

const sortOptionList = [
  { value: "latest", name: "최신순" },
  { value: "oldest", name: "오래된 순" },
];
const optionMoodList = [
  { value: "all", name: "모두" },
  { value: "good", name: "좋은 감정만" },
  { value: "bad", name: "안좋은 감정만" },
];
const ConrolMenu = ({ value, onChange, optionList, className }) => {
  return (
    <select
      className={className}
      value={value}
      onChange={(e) => onChange(e.target.value)}
    >
      {optionList.map((item, idx) => (
        <option key={idx} value={item.value}>
          {item.name}
        </option>
      ))}
    </select>
  );
};

export default function MainList({ diaryList }) {
  const setInfo = useContext(handleEditContext);
  const handleEdit = (item) => {
    setInfo.setEditInfo(item.id, item.date, item.content, item.emotion);
  };
  const [sortType, setSortType] = useState("lastest");
  const [filterType, setFilterType] = useState("all");
  const getProcessedDiaryList = () => {
    const compare = (a, b) => {
      if (sortType === "latest") {
        return parseInt(b.date) - parseInt(a.date);
      } else {
        return parseInt(a.date) - parseInt(b.date);
      }
    };
    const copyList = JSON.parse(JSON.stringify(diaryList));
    const sortedList = copyList.sort(compare);
    return sortedList;
  };
  return (
    <>
      <div className="control-bar">
        <ConrolMenu
          value={sortType}
          onChange={setSortType}
          optionList={sortOptionList}
          className={`select-date`}
        />
        <ConrolMenu
          value={filterType}
          onChange={setFilterType}
          optionList={optionMoodList}
          className={`select-mood`}
        />
        <Link to="/new">
          <Button
            type={"positive"}
            text={"새 일기쓰기"}
            className={"btn-create"}
          />
        </Link>
      </div>
      {getProcessedDiaryList().map((item, idx) => {
        return (
          <li
            key={idx}
            className="list"
            onClick={() => {
              handleEdit(item);
            }}
          >
            <div className="list-img">
              <Moods mood={item.emotion} />
            </div>
            <div className="list-content">
              <div className="content">
                <div className="content-date">
                  {`${new Date(item.date).getFullYear()}. ${
                    new Date(item.date).getMonth() + 1
                  } .${new Date(item.date).getDate()}`}
                </div>
                <div className="content-text">{item.content}</div>
              </div>
              <div className="content-edit-btn">
                <Link to="/edit">
                  <Button
                    text={"수정하기"}
                    type={"default"}
                    onClick={handleEdit}
                  />
                </Link>
              </div>
            </div>
          </li>
        );
      })}
    </>
  );
}
