import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { handleEditContext } from "../App";
import { Button } from "./Button";
import { Moods } from "./Moods";

const ConrolMenu = ({ value, onChange, optionList }) => {
  return <select></select>;
};

export default function MainList({ diaryList }) {
  const setInfo = useContext(handleEditContext);
  const handleEdit = (item) => {
    setInfo.setEditInfo(item.id, item.date, item.content, item.emotion);
  };
  const [sortType, setSortType] = useState("lastest");

  return (
    <>
      <ConrolMenu value={sortType} />
      <div className="control-bar">
        <select className="option-date">
          <option value="최신순">최신순</option>
          <option value="오래된 순">오래된 순</option>
        </select>
        <select className="option-mood">
          <option value="모두">모두</option>
          <option value="좋은 기억만">좋은 기억만</option>
          <option value="안좋은 기억만">안좋은 기억만</option>
        </select>
        <Link to="/new">
          <Button
            type={"positive"}
            text={"새 일기쓰기"}
            className={"btn-create"}
          />
        </Link>
      </div>
      {diaryList.map((item, idx) => {
        return (
          <li
            className="list"
            onClick={() => {
              handleEdit(item);
            }}
          >
            <div className="list-img">
              <Moods mood={item.emotion} />
            </div>
            <div className="list-content">
              <div className="content">
                <div className="content-date">
                  {`${new Date(item.date).getFullYear()}. ${
                    new Date(item.date).getMonth() + 1
                  } .${new Date(item.date).getDate()}`}
                </div>
                <div className="content-text">{item.content}</div>
              </div>
              <div className="content-edit-btn">
                <Link to="/edit">
                  <Button
                    text={"수정하기"}
                    type={"default"}
                    onClick={handleEdit}
                  />
                </Link>
              </div>
            </div>
          </li>
        );
      })}
    </>
  );
}
